import React, { useState, useEffect } from 'react';

export default function AuthModal({ onClose }) {
  const [isLogin, setIsLogin] = useState(true);
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    // Додаємо клас 'show' після монтування
    setTimeout(() => setVisible(true), 10);
  }, []);

  const handleClose = () => {
    setVisible(false);
    // Чекаємо завершення анімації перед закриттям
    setTimeout(onClose, 300);
  };

  return (
    <div className={`auth-modal ${visible ? 'show' : ''}`}>
      <div className='auth-content'>
        <button className='close-btn' onClick={handleClose}>×</button>
        <div className='auth-tabs'>
          <button onClick={() => setIsLogin(true)} className={isLogin ? 'active' : ''}>Увійти</button>
          <button onClick={() => setIsLogin(false)} className={!isLogin ? 'active' : ''}>Реєстрація</button>
        </div>

        {isLogin ? (
          <form className='auth-form'>
            <input type='email' placeholder='Email' required />
            <input type='password' placeholder='Пароль' required />
            <button type='submit'>Увійти</button>
          </form>
        ) : (
          <form className='auth-form'>
            <input type='text' placeholder="Ім'я" required />
            <input type='email' placeholder='Email' required />
            <input type='password' placeholder='Пароль' required />
            <button type='submit'>Зареєструватися</button>
          </form>
        )}
      </div>
    </div>
  );
}
